check.codes:
if not proposal.vote_post_id:
	return None
yes_votes = proposal.get_yes_votes().count()
no_votes = proposal.get_no_votes().count()
if(yes_votes == 1 and no_votes == 0):
	return PASSED
elif(yes_votes == 0 and no_votes == 1):
	return FAILED
return PROPOSED

if not variables.max_rank:
	proposal.data.set("max_rank", len(variables.candidates))
else:
	proposal.data.set("max_rank", variables.max_rank)

if not variables.rank_scores:
	max_rank = proposal.data.get("max_rank")
	proposal.data.set("rank_scores", [max_rank - i for i in range(max_rank)])
else:
	proposal.data.set("rank_scores", variables.rank_scores)
	if len(variables.rank_scores) != proposal.data.get("max_rank"):
		logger.debug("The list of rank scores should have the length of max_rank.")
		return FAILED

if not proposal.data.get("options"):
	proposal.data.set("options", [str(i) for i in range(1, proposal.data.get("max_rank") + 1)])

if not proposal.vote_post_id:
	# initialize stage, use default value for variables that are not specified by users
	if not variables.eligible_voters:
		proposal.data.set("eligible_voters", [user.username for user in slack.get_users_in_channel(variables.vote_channel)])
		logger.debug(f"eligible users are {[str(user) for user in slack.get_users_in_channel(variables.vote_channel)]}")
	else:
		proposal.data.set("eligible_voters", variables.eligible_voters)
	return None 

if not proposal.vote_post_id:
	return None

if proposal.get_time_elapsed().total_seconds() > variables.vote_duration:
	outcomes = proposal.get_select_votes_by_users()
	valid_users = []
	for user in proposal.data.get("eligible_voters"):
		if user not in outcomes:
			continue
		votes = outcomes.get(user, {})
		rank_options = proposal.data.get("options")
		candidates = variables.candidates
		if len(votes) != len(set(votes.values())):
			slack.post_message(f"You ranked two candidates in the same position, and therefore your vote will be discarded", users=[user], post_type="ephemeral", channel=variables.vote_channel)
		elif len(candidates) > len(rank_options) and len(votes) != len(rank_options):
			# when people are asked to choose top N candidates from more candidates, they are expected to give out all ranks
			slack.post_message(f"You are expected to choose the top {variables.max_rank} candidates, but you only ranked {len(votes)}. So your vote will be discarded.", users=[user], post_type="ephemeral", channel=variables.vote_channel)
		elif len(candidates) < len(rank_options) and len(votes) != len(candidates):
			# when people are asked to rank all candidates
			slack.post_message(f"You are expected to rank all candidates, but you only ranked {len(votes)} out of {len(candidates)}. So your vote will be discarded.", users=[user], post_type="ephemeral", channel=variables.vote_channel)
		else:
			slack.post_message(f"You have successfully ranked candidates and therefore your votes are valid", users=[user], post_type="ephemeral", channel=variables.vote_channel)
			valid_users.append(user)
	logger.debug(f"valid users {valid_users}")
	outcomes_by_candidates = proposal.get_select_votes_by_candidates(users=valid_users)
	logger.debug(f"outcomes_by_candidates {outcomes_by_candidates}")
	candidates_scores = []
	rank_scores = proposal.data.get("rank_scores")
	for candidate, votes in outcomes_by_candidates.items():
		scores = 0
		for index in range(len(rank_options)):
			rank = rank_options[index]
			if rank in votes:
				scores += rank_scores[index] * len(votes[rank])
		candidates_scores.append((candidate, scores))
	candidates_scores = sorted(candidates_scores, key=lambda x: x[1], reverse=True)
	logger.debug(f"ordered candidates scores {candidates_scores}")
	potential_winners = [candidate_pair[0] for candidate_pair in candidates_scores if candidate_pair[1] == candidates_scores[0][1]]
	if len(potential_winners) > 1:
		slack.post_message("There are ties in this vote and therefore the procedure fails", post_type='channel', channel=variables.vote_channel, thread_ts=proposal.vote_post_id, reply_broadcast=True)
		return FAILED
	elif len(potential_winners) == 0:
		slack.post_message("There are no vote at all and therefore the procedure fails", post_type='channel', channel=variables.vote_channel, thread_ts=proposal.vote_post_id, reply_broadcast=True)
		return FAILED
	else:
		proposal.data.set("winner", potential_winners[0])
		candidate_scores_summary = ", ".join([f"{candidate_pair[1]} for {candidate_pair[0]}" for candidate_pair in candidates_scores])
		slack.post_message(f"The candidate **{potential_winners[0]}** stands out in this ranked vote. Scores for each candidate are {candidate_scores_summary}" , post_type='channel', channel=variables.vote_channel, thread_ts=proposal.vote_post_id, reply_broadcast=True)
		return PASSED
return PROPOSED

if not proposal.data.get("options"):
	proposal.data.set("options", [str(i) for i in range(1, int(math.sqrt(variables.vote_budget)) + 1)])

if not proposal.data.get("eligible_voters"):
	if not variables.eligible_voters:
		proposal.data.set("eligible_voters", [user.username for user in slack.get_users()])
	else:
		proposal.data.set("eligible_voters", variables.eligible_voters)

if not proposal.vote_post_id:
	return None

if proposal.get_time_elapsed().total_seconds() > variables.vote_duration:
	outcomes = proposal.get_select_votes_by_users()
	valid_users = []
	for user in proposal.data.get("eligible_voters"):
		if user not in outcomes:
			slack.post_message(f"Please remember that you have not voted yet", users=[user], post_type="ephemeral", channel=variables.vote_channel)
			continue
		votes = outcomes.get(user, {})
		sum_of_cost = 0
		for candidate, vote in votes.items():
			sum_of_cost += int(vote) * int(vote)
		if sum_of_cost > variables.vote_budget:
			slack.post_message(f"You have used voting budget {sum_of_cost - variables.vote_budget} more than allocated, and therefore your vote will be discarded", users=[user], post_type="ephemeral", channel=variables.vote_channel)
		else:
			slack.post_message(f"You have used {sum_of_cost} voting budget below the limit {variables.vote_budget} and therefore your vote is valid", users=[user], post_type="ephemeral", channel=variables.vote_channel)
			valid_users.append(user)
	logger.debug(f"valid users {valid_users}")
	outcomes_by_candidates = proposal.get_select_votes_by_candidates(users=valid_users)
	logger.debug(f"outcomes_by_candidates {outcomes_by_candidates}")
	candidates_scores = []
	for candidate, votes in outcomes_by_candidates.items():
		scores = 0
		for option, voters_list in votes.items():
			scores += int(option) * len(voters_list)
		candidates_scores.append((candidate, scores))
	candidates_scores = sorted(candidates_scores, key=lambda x: x[1], reverse=True)
	logger.debug(f"ordered candidates scores {candidates_scores}")
	potential_winners = [candidate_pair[0] for candidate_pair in candidates_scores if candidate_pair[1] == candidates_scores[0][1]]
	proposal.data.set("winners", potential_winners)
	if len(potential_winners) > 1:
		return FAILED
	else:
		return PASSED
return PROPOSED

if proposal.data.get("delegate_stage") is None:
	proposal.data.set("delegate_stage", True)

if not proposal.data.get("username_dict") or not proposal.data.get("readable_name_dict"):
	all_users_dict = {user.username: str(user) for user in slack.get_users()}
	username_dict = {username: all_users_dict[username] for username in variables.eligible_voters}
	readable_name_dict = {readable_name: username for username, readable_name in username_dict.items()}
	proposal.data.set("username_dict", username_dict)
	proposal.data.set("readable_name_dict", readable_name_dict)
	proposal.data.set("eligible_voters_with_readable_names", [all_users_dict[username] for username in variables.eligible_voters])
if not proposal.vote_post_id:
	return None


if proposal.data.get("delegate_stage") and proposal.get_time_elapsed().total_seconds() > variables.delegate_duration:
	logger.debug("We are now at the delegation process")
	readable_name_dict = proposal.data.get("readable_name_dict")
	username_dict = proposal.data.get("username_dict")

	outcomes = proposal.get_select_votes_by_users()
	fake_option = "Your representative for this proposal"
	delegates_dict = {}
	proxy_dict = {}
	for user in variables.eligible_voters: # iterate all usernames, including those who have not voted
		votes = outcomes.get(user, {})
		if votes.get(fake_option, {}) and readable_name_dict.get(votes[fake_option], "") != user:
			delegates_dict[user] = readable_name_dict[votes[fake_option]] # converted to user id
			slack.post_message(f"You have delegated your votes to {votes[fake_option]}", users=[user], post_type="ephemeral", channel=variables.procedure_channel)
		else:
			delegates_dict[user] = user
			proxy_dict[user] = [user]

    # TODO: we need to decide whether there is a loop in this directed graph
	for voter, delegate in delegates_dict.items():
		if voter not in proxy_dict: # they delegate their votes to others
			final_delegate = delegate
			while final_delegate not in proxy_dict:
				final_delegate = delegates_dict[final_delegate]
			proxy_dict[final_delegate].append(voter)
	for proxy, delegate_list in proxy_dict.items():
		readable_names = ", ".join(username_dict[user] for user in delegate_list)
		slack.post_message(f"When making decision on this proposal, please remember that you are representing the following users {readable_names}", post_type="ephemeral", users=[proxy], channel=variables.procedure_channel)
	logger.debug("representatives: " + ", ".join([username_dict[proxy] for proxy in proxy_dict.keys()]))
	slack.initiate_vote(users=[key for key in proxy_dict.keys()], post_type="channel", text="We have collected people's delegation results, and now let's vote on this proposal", channel=variables.procedure_channel)
	proposal.data.set("proxy_dict", proxy_dict)
	proposal.data.set("delegate_stage", False)
	return PROPOSED

if not proposal.data.get("delegate_stage") and proposal.get_time_elapsed().total_seconds() > (variables.delegate_duration + variables.vote_duration):
	logger.debug("We are now at the actual voting process")
	proxy_dict = proposal.data.get("proxy_dict")
	yes_votes = proposal.get_yes_votes()
	weighted_yes_votes_sum = 0
	for vote in yes_votes:
		weighted_yes_votes_sum += len(proxy_dict[vote.user.username])
	no_votes = proposal.get_no_votes()
	weighted_no_votes_sum = 0
	for vote in no_votes:
		weighted_no_votes_sum += len(proxy_dict[vote.user.username])
	slack.post_message(f"There are in total {weighted_yes_votes_sum} yes votes and {weighted_no_votes_sum} no votes")
	if weighted_yes_votes_sum > weighted_no_votes_sum:
		return PASSED
	else:
		return FAILED
return PROPOSED
